import React ,{useState, useEffect} from 'react'
import { FilmCard } from './Components/filmCard';
import { SearchPage } from './Pages/searchPage';

function App() {
  const [movie, setData] = useState([{ }]);

  useEffect(() => {
    fetch("/api").then(
      res =>  res.json()
        ).then(data => {
      setData(data)
    }
    )
  }, [])


  return (
    <div>
      {(typeof movie != 'undefined') ? <FilmCard movie={movie} /> : <p>Loading...</p>}
    </div>
  )
}

export default App
